import React from "react";

import imageCompression from "browser-image-compression";

import Card from "react-bootstrap/Card";

export default class imageCompressor extends React.Component {
  constructor() {
    super();
    this.state = {
      compressedLink:
        "http://navparivartan.in/wp-content/uploads/2018/11/placeholder.png",
      originalImage: "",
      originalLink: "",
      clickedASR: false,
      clickedTTS: false,
      uploadImage: false,
      outputText: "",
      file : "",
      inputText : "",
      language: "en"
    };
  }

  handle = e => {
    this.setState({
      file : "file path", inputText : e.target.value
    });
  };
  handleLang = e => {
    this.setState({
      language : e.target.value
    });
  };

  changeValue = e => {
    this.setState({ [e.target.name]: e.target.value });
  };

  clickASR = e => {
    e.preventDefault();
    this.setState({ clickedASR: true, outputText : "TODO: PROCESS ASR" });
    return 1;
  };
  clickTTS = e => {
    e.preventDefault();
    this.setState({ clickedTTS: true});
    return 1;
  };

  render() {
    return (
      <div className="m-5">
        <div className="text-light text-center">
          <h3>1. Upload Wav File or Input Text</h3>
          <h3>2. Click on ASR or TTS</h3>
        </div>

        <div className="row mt-5 text-center">
          <div className="col-xl-4 col-lg-4 col-md-12 col-sm-12 ">
            {
              "Input Wav File"
            }
            <div className="d-flex justify-content-center">
              <input
                type="file"
                className="mt-2 btn btn-dark w-75"
                onChange={e => this.handle(e)}
              />
            </div>
          </div>
          <div className="col-xl-4 col-lg-4 col-md-12 mb-5 mt-5 col-sm-12 d-flex justify-content-center align-items-baseline">
            <br />
            {(
              <button
                type="button"
                className=" btn btn-dark"
                onClick={e => this.clickASR(e)}
                //compress button
              >
                ASR
              </button>
            )}
          </div>


          <div className="col-xl-4 col-lg-4 col-md-12 col-sm-12 mt-3">



            {this.state.clickedASR ? (
              //second image
              <div className="d-flex justify-content-center">
                {this.state.outputText}
              </div>
            ) : (
              <></>
            )}
          </div>
        </div>
        <div className="row mt-5">
        <div className="col-xl-4 col-lg-4 col-md-12 col-sm-12 text-center">
            {
              "Language"
            }
            <div className="d-flex justify-content-center">
              <input
                type="text"
                className="mt-2 w-75"
                onChange={e => this.handleLang(e)}
                value = {this.state.inputText}
              />
            </div>
          </div>
          <div className="col-xl-4 col-lg-4 col-md-12 col-sm-12 text-center">
            {
              "Input text"
            }
            <div className="d-flex justify-content-center">
              <input
                type="text"
                className="mt-2 w-75"
                onChange={e => this.handle(e)}
                value = {this.state.inputText}
              />
            </div>
          </div>
          <div className="col-xl-4 col-lg-4 col-md-12 mb-5 mt-5 col-sm-12 d-flex justify-content-center align-items-baseline">
            <br />
            {(
              <button
                type="button"
                className=" btn btn-dark"
                onClick={e => this.clickTTS(e)}
                //compress button
              >
                TTS
              </button>
            )}
          </div>


          <div className="col-xl-4 col-lg-4 col-md-12 col-sm-12 mt-3">



            {this.state.clickedTTS ? (
              //second image
              <div className="d-flex justify-content-center">
                {"TODO: RETURN AUDIO FILE"}
              </div>
            ) : (
              <></>
            )}
          </div>
        </div>
      </div>
    );
  }
}
