import React from "react";
import Select from 'react-select';
import imageCompression from "browser-image-compression";

import Card from "react-bootstrap/Card";

export default class imageCompressor extends React.Component {
  uploadAction() {
    var data = new FormData();
    var imagedata = document.querySelector('input[type="file"]').files[0];
    data.append("data", imagedata);
    console.log(data)
  }
  constructor() {
    super();
    this.state = {
      compressedLink:
        "http://navparivartan.in/wp-content/uploads/2018/11/placeholder.png",
      originalImage: "",
      originalLink: "",
      clickedASR: false,
      clickedTTS: false,
      uploadImage: false,
      outputText: "",
      file : "",
      inputText : "",
      language: ""
    };
  }

  handle = e => {
    console.log(e.target.value)
    this.uploadAction()
    this.setState({
      file : e.target.value
    });
  };
  handleLang = e => {
    this.setState({
      language : e
    });
  };

  changeValue = e => {
    this.setState({ [e.target.name]: e.target.value });
  };

  clickASR = e => {
    e.preventDefault();
    this.setState({ clickedASR: true, outputText : "TODO: PROCESS ASR" });
    return 1;
  };
  clickTTS = e => {
    e.preventDefault();
    this.setState({ clickedTTS: true});
    return 1;
  };



  render() {
    let techCompanies 
  techCompanies = [
    { label: "English", value: 1 },
    { label: "Spanish", value: 2 },
  ];
    return (
      <div className="m-5">
        <div className="text-light text-center">
          <h3>1. Input Language</h3>
          <h3>2. Upload Wav File or Input Text</h3>
          <h3>3. Click on ASR or TTS</h3>
        </div>

        <div className="row mt-5 text-center">
        <div className="col-xl-4 col-lg-4 col-md-12 col-sm-12 text-center">
        <p className="text-white">Language</p>

            <div className="d-flex justify-content-center">
              {/* <input
                type="text"
                className="mt-2 w-75"
                onChange={e => this.handleLang(e)}
                value = {this.state.language}
              /> */}
              <Select options={ techCompanies }
               onInputChange={e => this.handleLang(e)}
              // value = {this.state.language} 
              />
            </div>
          </div>
          <div className="col-xl-4 col-lg-4 col-md-12 col-sm-12 mt-5">
            <p className="text-white">Input Wav File</p>


            <div className="d-flex justify-content-center">
              {/* TODO: Handle audio file */}
              <input
                type="file"
                className=" btn btn-light w-75"
                onChange={e => this.handle(e)}
              />
            </div>
          </div>

          <div className="col-xl-4 col-lg-4 col-md-12 mt-2 col-sm-12 d-flex justify-content-center align-items-baseline">
            <br />
            {(
              <button
                type="button"
                className=" btn btn-dark"
                onClick={e => this.clickASR(e)}
                //compress button
              >
                ASR
              </button>
            )}
          </div>


          <div className="col-xl-4 col-lg-4 col-md-12 col-sm-12">
            {this.state.clickedASR ? (
              //second image
              <div className="d-flex justify-content-center text-white">
                {this.state.outputText}
              </div>
            ) : (
              <></>
            )}
          </div>
        </div>
        <div className="row mt-5">
          <div className="col-xl-4 col-lg-4 col-md-12 col-sm-12 text-center">
          <p className="text-white">Input Text </p>
            <div className="d-flex justify-content-center">
              <input
                type="text"
                className="mt-2 w-75"
                onChange={e => this.handle(e)}
                value = {this.state.inputText}
              />
            </div>
          </div>
          <div className="col-xl-4 col-lg-4 col-md-12 mt-2 col-sm-12 d-flex justify-content-center align-items-baseline">
            <br />
            {(
              <button
                type="button"
                className=" btn btn-dark"
                onClick={e => this.clickTTS(e)}
              >
                TTS
              </button>
            )}
          </div>

          <div className="col-xl-4 col-lg-4 col-md-12 col-sm-12 mt-3">
            {this.state.clickedTTS ? (
              //second image
              <div className="d-flex justify-content-center text-white">
                {"TODO: RETURN AUDIO FILE"}
              </div>
            ) : (
              <></>
            )}
          </div>
        </div>
      </div>
    );
  }
}
